DELETE FROM tafsirs WHERE ayah_id = 1 AND kitab = 'kemenag_ringkas';
INSERT INTO tafsirs (ayah_id, kitab, text) VALUES (1, 'kemenag_ringkas', '_Alif Laam miim_. Beberapa surah dalam Al-Qur''an dibuka dengan huruf abjad seperti _Alif Laam miim, Alif Laam Raa_&nbsp;, dan sebagainya. Makna huruf-huruf itu hanya Allah yang tahu. Ada yang berpendapat bahwa huruf-huruf itu adalah nama surah dan ada pula yang berpendapat bahwa gunanya untuk menarik perhatian, atau untuk menunjukkan mukjizat Al-Qur''an, karena Al-Qur''an disusun dari rangkaian hurufhuruf abjad yang digunakan dalam bahasa bangsa Arab sendiri. Meskipun demikian, mereka tidak pernah mampu untuk membuat rangkaian huruf-huruf itu menjadi seperti Al-Qur''an.  &nbsp;  &nbsp;')