DELETE FROM tafsirs WHERE ayah_id = 139 AND kitab = 'kemenag_ringkas';
INSERT INTO tafsirs (ayah_id, kitab, text) VALUES (139, 'kemenag_ringkas', 'Salah satu faktor yang membuat kedudukan Nabi Ibrahim tinggi di dunia dan akhirat adalah Islam, yaitu penyerahan diri sepe nuhnya kepada Allah. _Dan Ibrahim_ pun _mewasiatkan_ ajaran penyerahan diri _itu kepada anak-anaknya_, Ismail dan Ishak. _Demikian pula Yakub_, ia berwasiat kepada anak-anaknya," _Wahai anak-anakku! Sesungguhnya Allah telah memilih agama_ penyerahan diri_ ini untukmu, maka janganlah kamu mati kecuali dalam keadaan muslim_ yang berserah diri.')