DELETE FROM tafsirs WHERE ayah_id = 4403 AND kitab = 'ibnu_katsir';
INSERT INTO tafsirs (ayah_id, kitab, text) VALUES (4403, 'ibnu_katsir', 'Allah Swt. berfirman, menceritakan perihal Nabi Ibrahim a.s.:  Maka tatkala rasa takut hilang dari Ibrahim dan berita gembira telah datang kepadanya, dia pun bersoal jawab dengan (malaikat-malaikat) Kami tentang kaum Lut. Sesungguhnya Ibrahim itu benar-benar seorang yang penyantun lagi pengiba dan suka kembali kepada Allah. Hai Ibrahim, tinggalkanlah soal jawab ini, sesungguhnya telah datang ketetapan Tuhanmu, dan sesungguhnya mereka itu akan didatangi azab yang tidak dapat ditolak. (Hud: 74-76)  Dan dalam surat ini disebutkan oleh firman-Nya:  Ibrahim bertanya, ''Apakah urusanmu, hai para utusan?â€ (Adz-Dzariyat: 31)  Yakni apakah urusanmu dan tugas apakah yang menyebabkan kamu datang kemari?')